    ProcessModeler.prototype.setLabelState = function () {
        var self = this;
        var modeler = self.modeler.modeler;
        var eventBus = modeler.get('eventBus');
        var moddle = modeler.get('moddle');
        var modeling = modeler.get('modeling');
        var orgName, changedName, extensionElements;

        eventBus.on('commandStack.element.updateLabel.postExecute', function (e) {
            var extensionData;
            orgName = e.context.oldLabel;
            changedName = e.context.newLabel;

            if (e.context.element.businessObject.extensionElements && e.context.element.businessObject.extensionElements.get('menu')) {
                extensionData = {
                    menu: e.context.element.businessObject.extensionElements.get('menu'),
                    name: e.context.element.businessObject.extensionElements.get('name')
                };

                if (orgName !== changedName) {
                    extensionData.changed = true;
                }
            } else if (e.context.element.businessObject.extensionElements && e.context.element.businessObject.extensionElements.get('process')) {
                extensionData = {
                    process: e.context.element.businessObject.extensionElements.get('process')
                };

                if (orgName !== changedName) {
                    extensionData.changed = true;
                }
            } else {
                extensionData = {};

                if (orgName !== changedName) {
                    extensionData.changed = true;
                }
            }

            if (e.context.element.businessObject.extensionElements && e.context.element.businessObject.extensionElements.get('key')) {
                extensionData.key = e.context.element.businessObject.extensionElements.get('key');
            }
            extensionElements = moddle.create('bpmn:ExtensionElements', extensionData);
            modeling.updateProperties(e.context.element, {extensionElements: extensionElements});
        });
    };

