    /**
     * 그리드 특정 컬럼 옵션 변경하고 그리드에 즉시 반영
     * @description 첫번째 인자로 등록하는 필드명은 기존 그리드에 존재하는 것 중 하나여야 한다.
     * @param {string} columnFieldName - 변경하고자 하는 컬럼의 필드명
     * @param {object} nextColumnOption - 변경하고자 하는 컬럼의 옵션 객체
     * @returns {undefined}
     */
    TreeGrid.prototype.setColumn = function(columnFieldName, nextColumnOption) {
        var self = this,
            prevColumn,
            nextColumn,
            newColumn,
            columns = self.options.columns,
            result;

        delete nextColumnOption.field;

        // PD1-931 realGrid setColumn width 속성에 대한 처리가 정상적으로 되지 않아 width 에 대해선 setColumnProperty API 사용
        if (nextColumnOption.width) {
            self._grid.setColumnProperty(columnFieldName, 'width', nextColumnOption.width);
            delete nextColumnOption.width;
        }

        // PD1-1526 realGrid setColumn visible: true 에 대한 처리가 정상적으로 되지 않아 visible 에 대해서 setColumnProperty API 사용
        if (nextColumnOption.visible) {
            self._grid.setColumnProperty(columnFieldName, 'visible', nextColumnOption.visible);
        }

        // region PD1-1561-그리드 setColumn API 로 column 의 editable 을 true 로 설정 시 적용되지 않는 문제 때문에 수정
        var userColumn;
        if ($.type(self.userOptions.columns) === 'array') {
            userColumn = findUserOptionsColumn(self.userOptions.columns, columnFieldName);
        }

        // 변경한 옵션 값을 userOption 에 넣어줌
        userColumn = $.extend(true, userColumn, nextColumnOption);

        // 파라미터 값으로 column.editable 만 설정한 경우
        if (nextColumnOption.editable !== undefined
            && (nextColumnOption.editor === undefined || nextColumnOption.editor.editable === undefined)) {
            // 사용자가 설정한 editor.editable 값이 없을 경우
            if ((userColumn.editor === undefined || userColumn.editor.editable === undefined)) {
                $.extend(true, nextColumnOption, {editor: {editable: nextColumnOption.editable}});
                // 사용자가 설정한 editor.editable 값이 있을 경우
            } else if (userColumn.editor && userColumn.editor.editable !== undefined) {
                $.extend(true, nextColumnOption, {editor: {editable: userColumn.editor.editable}});
            }
        }

        // 파라미터 값으로 editor.editable 만 설정한 경우
        if (nextColumnOption.editable === undefined && nextColumnOption.editor && nextColumnOption.editor.editable !== undefined) {
            // 사용자가 설정한 column.editable 값이 있을 경우
            if (userColumn !== undefined && userColumn.editable !== undefined) {
                $.extend(true, nextColumnOption, {editable: userColumn.editable});
                // 사용자가 설정한 column.editable 값이 없을 경우
            } else {
                $.extend(true, nextColumnOption, {editable: self.options.editable});
            }
        }
        // endregion

        findColumnAndExtend(columns);

        self.options.styleColumn = [];
        __initialize(self);
        result = customizingColumn(columns, self.options, self);

        dews.ui.gridUtil._setColumns(self, result);
        prevColumn = self._grid.columnByName(columnFieldName);
        nextColumn = self.columns[columnFieldName];

        // PD1-1752-그리드-setColumn API 를 이용하여 드롭다운리스트의 데이터소스 변경 시 기존 데이터가 남아있는 문제때문에 추가한 코드
        if (nextColumn && nextColumn.labels && nextColumn.values
            && nextColumn.editor && nextColumn.editor.type === 'dropDown' && prevColumn) {
            prevColumn.labels = [];
            prevColumn.values = [];
        }

        newColumn = $.extend(true, {}, prevColumn, nextColumn);
        self._grid.setColumn(newColumn);

        self._filterManager.setFilterActions(self);

        self._setCriteria();

        function findColumnAndExtend (columns) {
            for (var i=0; i < columns.length; i++) {
                if (columns[i].columns) {
                    findColumnAndExtend(columns[i].columns)
                } else {
                    if(columns[i].fieldName && columns[i].fieldName === columnFieldName) {
                        $.extend(true, columns[i], nextColumnOption);
                    }
                }
            }
        }
    };

