            // 잘못된 값 입력 시에 잘못된 값 초기화
            // var date = new Date();
            // var now = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));

            // if (parseInt(yyyy) > 9999 || parseInt(yyyy) <= 999) {
